mapper.select:
SELECT id, user_code AS "userCode", user_password AS "userPassword", user_name AS "userName", user_desc AS "userDesc", created_by AS "createdBy", created_date AS "createdDate", updated_by AS "updatedBy", updated_date AS "updatedDate"
FROM user_info where 1=1
and user_code = #{userCode, jdbcType=VARCHAR}
AND user_name LIKE "%"#{userName, jdbcType=VARCHAR}"%"
and created_date >= #{createdDateStart, jdbcType=VARCHAR} and created_date <= #{createdDateEnd, jdbcType=VARCHAR}
order by updated_date desc
